document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".header__burger"),t=document.querySelector(".header__menu-body");e.addEventListener("click",(function(){if(t.classList.contains("active"))t.style.maxHeight="0",t.classList.remove("active");else{t.style.maxHeight="none";const e=getComputedStyle(t).height;t.style.maxHeight="0",t.classList.add("active"),setTimeout((()=>{t.style.height=e}),0)}}))}));